
def fn_cezar(cistopis, kljuc, razdeljeno=False):
    sifropis =''

    for znak in cistopis:
        if razdeljeno:
            if znak.islower():
                sifropis += chr((ord(znak) - ord('a') + kljuc) % 26 + ord('a'))
            elif znak.isupper():
                sifropis += chr((ord(znak) - ord('A') + kljuc) % 26 + ord('A'))
            elif znak.isnumeric():
                sifropis += chr((ord(znak) - ord('0') + kljuc) % 9 + ord('0'))
        else:
            sifropis += chr((ord(znak) - 32 + kljuc) % 95 + 32)

    return sifropis


def vigener(cistopis, kljuc, sifriraj=True):
    sifropis = ''
    k = 0
    for i, char in enumerate(cistopis):
        rotacija = ord(kljuc[k]) - ord('A')
        if not sifriraj:
            rotacija *= -1
        sifropis += fn_cezar(char, rotacija)
        k += 1
        if k > len(kljuc)-1:
            k = 0
    return sifropis


def fn_substitucija(cistopis, smer=True):
    kljuc = 'ABCDEFGHIJKLMNOPRSTUWXYZ'
    abeceda = 'FWOMJZDREAUGNYLKICTSPXHB'
    if cistopis.islower():
        kljuc = kljuc.lower()
        abeceda = abeceda.lower()
    sifropis=''
    for i, char in enumerate(cistopis):
        if char in [' ', ',', '.']:
            sifropis += char
        elif smer:
            sifropis += kljuc[abeceda.index(char)]
        else:
            sifropis += abeceda[kljuc.index(char)]

    return sifropis

def naloga_8(sifropis, kljuc):

    cistopis =""
    for word in sifropis:
        cistopis += kljuc[word-1]
    return cistopis


